#include <bits/stdc++.h>using namespace std;struct event{	int t;	int type;};int main(){	int n;	cin>>n;	vector<event> v(2*n);	for(int i=0;i<2*n;i+=2) {		int a,b;		cin>>a>>b;		v[i]={a,1};		v[i+1]={b,-1};	}	sort(v.begin(), v.end(), [](event a, event b) {        if (a.t != b.t) return a.t < b.t;        return a.type>b.type;    });	int ans=0,cnt=0;	for(event i:v){		if(i.type==1){			cnt++;			ans=max(ans,cnt);		}		else cnt--;	}	cout << ans;}