#include <iostream>#include <vector>#include <string>using namespace std;vector<string> board(8);bool cols[8], diag1[15], diag2[15];int total_solutions = 0;void solve(int r) {    if (r == 8) {        total_solutions++;        return;    }    for (int c = 0; c < 8; c++) {        if (board[r][c] == '.' && !cols[c] && !diag1[r + c] && !diag2[r - c + 7]) {            cols[c] = diag1[r + c] = diag2[r - c + 7] = true;            solve(r + 1);            cols[c] = diag1[r + c] = diag2[r - c + 7] = false;        }    }}int main() {    for (int i = 0; i < 8; i++) {        if (!(cin >> board[i])) break;    }    solve(0);    cout << total_solutions << endl;}