#include <iostream>#include <vector>#include <unordered_map>using namespace std;int main() {    ios_base::sync_with_stdio(false),cin.tie(NULL);    int n;    long long x;    cin>>n>>x;    vector<int> v(n);    for(int i=0;i<n;i++) cin>>v[i];    int ans=0;    int l=0,cur=0;    for(int r=0;r<n;r++){		cur+=v[r];		while (cur > x && l <= r) {            cur-=v[l];            l++;        }		if (cur == x) {            ans++;        }    }    cout << ans;}