#include <bits/stdc++.h>using namespace std;typedef long long ll;vector<ll> v;ll k;bool chk(ll h){	ll cnt=0,ans=1;	for(ll i:v){		if(i>h) return false;		if(cnt+i>h) {			ans++;			cnt=0;		}		cnt+=i;	}	return ans<=k;}int main(){	int n;	cin>>n>>k;	long long r=0;	v.resize(n);	for(int i=0;i<n;i++) {		cin>>v[i];		r+=v[i];	}	long long ans=0;	for(ll i= (1LL<<(__lg(r)+1));i>0;i>>=1){		if(!chk(ans+i)){			ans+=i;		}	}	cout << ans+1;}