#include <iostream>#include <vector>#include <string>#include <unordered_set>using namespace std;int main() {    ios_base::sync_with_stdio(false),cin.tie(NULL);    int m;    if (!(cin >> m)) return 0;    vector<string> sticks(m);    unordered_set<string> pool;    for (int i = 0; i < m; i++) {        cin >> sticks[i];        pool.insert(sticks[i]);    }    int ans = 0;    for (int i = 0; i < m; i++) {        string &s = sticks[i];        int len = s.length();        for (int lenA = 1; lenA * 2 < len; lenA++) {            string prefixA = s.substr(0, lenA);            string suffixA = s.substr(len - lenA);            if (prefixA == suffixA) {                string targetB = s.substr(lenA, len - 2 * lenA);                if (pool.count(targetB)) {                    ans++;                }            }        }    }    cout << ans << endl;}