#include <bits/stdc++.h>using namespace std;int k;string s,b;unordered_set<string> us;bool FA = false;void dfs(string sub){	if(FA) return;	if (sub.length() == k) {        if (us.count(sub) == 0) {            cout << sub;            FA = true;        }        return;    }    for (char c : s) {        dfs(sub + c);        if (FA) return;    }}int main(){	cin>>s>>k>>b;	if (b.length() >= k) {        for (int i = 0; i <= b.length() - k; i++) {            string sub = b.substr(i, k);            us.insert(sub);        }    }    dfs("");}