#include <bits/stdc++.h>using namespace std;vector<long long> v;int n,k;bool chk(long long m){	long long cnt=0,ans=1;	for(long long i:v){		if(i>m) return false;		if(cnt+i>m){			ans++;			cnt=i;		}		else {			cnt+=i;		}	}	return ans<=k;}int main(){	cin>>n>>k;	v.resize(n);	for(int i=0;i<n;i++) cin>>v[i];	long long ans=0;	for(long long i=1LL<<60;i>0;i>>=1){		if(!chk(ans+i)){			ans+=i;		}	}	cout << ans+1;}