#include <bits/stdc++.h>using namespace std;struct movie{	int s,f;};int main(){	multiset<int> ms;	int n,k;	cin>>n>>k;	vector<movie> v(n);	for(int i=0;i<n;i++) cin>>v[i].s>>v[i].f;	sort(v.begin(),v.end(),[&](movie &a,movie &b){return a.f<b.f;});	for(int i=0;i<k;i++) ms.insert(0);	int ans=0;	for(movie &i:v){		auto it = ms.upper_bound(i.s);		if(it!=ms.begin()){			--it;			ms.erase(it);			ms.insert(i.f);			ans++;		}	}	cout << ans;}