#include <bits/stdc++.h>using namespace std;const int N = 200005;vector<int> adj[N];int main(){	int n,m;	cin>>n>>m;	for(int i=0;i<m;i++){		int u,v;		cin>>u>>v;		adj[u].push_back(v);		adj[v].push_back(u);	}	int q;	cin>>q;	for(int i=1;i<=n;i++) sort(adj[i].begin(),adj[i].end());	while(q--){		int qs;		cin>>qs;		for(int i:adj[qs]) cout << i << " ";		cout << "\n";	}}