#include <bits/stdc++.h>using namespace std;const int N = 200005;vector<int> adj[N];bool vis[N];void dfs(int u,int p){	vis[u]=true;	for(int v:adj[u]){		if(v==p||vis[v]) continue;		dfs(v,u);	}}int main(){	int n,m;	cin>>n>>m;	for(int i=0;i<m;i++){		int a,b;		cin>>a>>b;		adj[a].push_back(b);		adj[b].push_back(a);	}	vector<int> res;	for (int i=1;i<=n;i++) {		if (!vis[i]) {			res.push_back(i);			dfs(i,0);		}	}	cout << res.size()-1 << "\n";	for(int i=1;i<res.size();i++) cout << res[0] << " " << res[i] << "\n";}