#include <bits/stdc++.h>using namespace std;const int N = 2000005;vector<int> adj[N];int group[N];bool chk;void dfs(int u){	if (chk) return;	for(int v:adj[u]){		if(group[v]==0){			group[v] = (group[u]==1?2:1);            dfs(v);            if(chk) return;		}		else{			if (group[v]==group[u]){				chk=true;				return;			}		}	}}int main(){	int n,m;	cin>>n>>m;	for(int i=0;i<m;i++){		int u,v;		cin>>u>>v;		adj[u].push_back(v);		adj[v].push_back(u);	}	for(int i=1;i<=n;i++){		if(group[i]==0){			group[i]=1;			dfs(i);		}	}	if(chk) cout << "IMPOSSIBLE";	else for(int i=1;i<=n;i++) cout << group[i] << " ";}