#include <bits/stdc++.h>using namespace std;const int N = 200005;vector<int> adj[N];bitset<N> vis;int main(){	int n,m;	cin>>n>>m;	for(int i=0;i<m;i++){		int u,v;		cin>>u>>v;		adj[u].push_back(v);		adj[v].push_back(u);	}	queue<int> q;	vector<int> dist(N,2e9);	q.push(1);	vis[1]=1;	dist[1]=0;	while(!q.empty()){		int u = q.front();		q.pop();		for(int v:adj[u]){			if(vis[v]) continue;			vis[v]=1;			dist[v] = dist[u]+1;			q.push(v);		}	}	if(!vis[n]) cout << "IMPOSSIBLE";	else cout << dist[n];}