#include <bits/stdc++.h>using namespace std;typedef pair<int,int> pii;const int N = 305;int grid[N][N];int dx[] = {1,-1,0,0};int dy[] = {0,0,1,-1};int n;int ans=1000000;vector<vector<bool>> vis;vector<vector<int>> dist;bool chk(int limit){	vis.assign(N,vector<bool>(N,false));	queue<pii> q;	vis[1][1]=true;	q.push({1,1});	while(!q.empty()){		pii pos = q.front();		q.pop();		if(pos.first==n&&pos.second==n) return true;		for(int i=0;i<4;i++){			int x= pos.first+dx[i],y=pos.second+dy[i];			if(x<1||x>n||y<1||y>n||vis[x][y]) continue;			if(abs(grid[x][y]-grid[pos.first][pos.second])<=limit){				vis[x][y]=1;				q.push({x,y});			}		}	}	return false;}void solve(){	dist.assign(N,vector<int>(N,-1));	queue<pii> q;	dist[1][1]=0;	q.push({1,1});	while(!q.empty()){		pii pos = q.front();		q.pop();		if(pos.first == n && pos.second == n) return;		for(int i=0;i<4;i++){			int x= pos.first+dx[i],y=pos.second+dy[i];			if(x<1||x>n||y<1||y>n) continue;			if(abs(grid[x][y]-grid[pos.first][pos.second])<=ans&&dist[x][y]==-1){				dist[x][y]=dist[pos.first][pos.second]+1;				q.push({x,y});			}		}	}}int main(){	cin>>n;	for(int i=1;i<=n;i++){		for(int j=1;j<=n;j++){			cin>>grid[i][j];		}	}	int L = 0, R = 1000000;    while(L<=R){        int mid = (L+R)/2;        if(chk(mid)){            ans = mid;            R = mid-1;        }        else {            L=mid+1;        }    }	solve();	cout << ans << "\n" << dist[n][n];}