#include <bits/stdc++.h>using namespace std;const int N = 1000005;int s[N];int q[N];int main(){    ios::sync_with_stdio(0),cin.tie(0);    int t;    cin>>t;    s[0] = 1 % 3;    s[1] = 1 % 3;    s[2] = 4 % 3;    q[0] = 5 % 3;    q[1] = 1 % 3;    q[2] = 4 % 3;    int ans = 0;    for(int i = 0; i <= 2 && i <= t; i++){        if(s[i] == 1) ans++;        if(q[i] == 1) ans++;    }    for(int i = 3; i <= t; i++){        s[i] = (q[i-3] + s[i-2]) % 3;        q[i] = (q[i-2] + s[i]) % 3;        if(s[i] == 1) ans++;        if(q[i] == 1) ans++;    }    cout << ans;}